import java.util.*;
import sailpoint.object.ProvisioningPlan.ObjectOperation;
import sailpoint.object.ProvisioningPlan.ObjectRequest;
import sailpoint.object.ProvisioningPlan.AttributeRequest;
import sailpoint.object.ProvisioningPlan.AccountRequest;
import sailpoint.api.*;
import sailpoint.object.*;
import sailpoint.tools.*;

      List accounts = plan.getAccountRequests();
      if (accounts != null)
      {
            for (AccountRequest req : accounts)
            {
              AccountRequest.Operation op = req.getOperation();

              if (op == AccountRequest.Operation.Delete)
              {
                    req.add(new AttributeRequest("MODE", "DELETE"));
                    req.add(new AttributeRequest("DELAY", "N"));
              }
              if (op == AccountRequest.Operation.Create)
              {
                  String userId = req.getNativeIdentity();
                  if (userId != null)
                  {
                    req.setNativeIdentity(userId.toUpperCase());
                  }
              }
              if(context != null)
              {
                IdentityService identityService = new IdentityService(context);
                if(application != null)
                {
                  String shouldUseMSAdminDefined = (String) application.getAttributeValue("useMSAdminDefined");
                  String msAdmin=null;
                  if(Util.isNotNullOrEmpty(shouldUseMSAdminDefined)) 
                  {
                    if(shouldUseMSAdminDefined.equals("Y"))
                    {
                      List idnList = plan.getRequesters();
                      if(idnList!=null)
                      {
                        if(!idnList.isEmpty())
                        {
                          Identity idn=(Identity) idnList.get(0);
                          List lnkList = identityService.getLinks(idn, application);
                          if(lnkList!=null)
                          {
                            if(!lnkList.isEmpty())
                            {
                              Link lnk = (Link) lnkList.get(0);
                              //System.out.println("Adding msAdmin in provisioning plan.");
                              req.addArgument("msAdmin", lnk.getDisplayableName());
                            }
                          }
                          else
                            System.out.println("Requester does not have a link from application " + application.getName());
                        }
                      }
                      else
                        System.out.println("No requester found.");
                    }
                  }
                  //else
                    //System.out.println("shouldUseMSAdminDefined is not set to Y");
                }
              }
            }
      }

    List groups = plan.getObjectRequests();
    if (groups != null)
    {
        for (ObjectRequest req : groups)
        {
            ObjectOperation op = req.getOp();
             if (op == ObjectOperation.Delete)
            {
                req.add(new AttributeRequest("MODE", "DELETE"));
            }
            if (op == ObjectOperation.Create)
            {
                String groupId = req.getNativeIdentity();
                if (groupId != null)
                {
                    req.setNativeIdentity(groupId.toUpperCase());
                }
            }
            List attrRequests = req.getAttributeRequests();
            if(attrRequests != null) {
                if(application != null) {
                    Schema grpSchema = application.getSchema("group");
                    if(grpSchema != null) {
                        if(grpSchema.getAttributeNames() != null) {
                            for (AttributeRequest attrreq : attrRequests) {
                                if(grpSchema.getAttributeNames().contains(attrreq.getName())) {
                                    if(attrreq.getValue() instanceof java.lang.String) {
                                        attrreq.setValue(attrreq.getValue().toUpperCase());
                                    }
                                }
                            }
                        }
                    }
                }
            }
            if(context != null)
            {
              IdentityService identityService = new IdentityService(context);
              if(application != null)
              {
                String shouldUseMSAdminDefined = (String) application.getAttributeValue("useMSAdminDefined");
                String msAdmin=null;
                if(Util.isNotNullOrEmpty(shouldUseMSAdminDefined)) 
                {
                  if(shouldUseMSAdminDefined.equals("Y"))
                  {
                    List idnList = plan.getRequesters();
                    if(idnList!=null)
                    {
                      if(!idnList.isEmpty())
                      {
                        Identity idn=(Identity) idnList.get(0);
                        List lnkList = identityService.getLinks(idn, application);
                        if(lnkList!=null)
                        {
                          if(!lnkList.isEmpty())
                          {
                            Link lnk = (Link) lnkList.get(0);
                            //System.out.println("Adding msAdmin in provisioning plan.");
                            req.addArgument("msAdmin", lnk.getDisplayableName());
                          }
                        }
                        else
                          System.out.println("Requester does not have a link from application " + application.getName());
                      }
                    }
                    else
                      System.out.println("No requester found.");
                  }
                }
                //else
                  //System.out.println("shouldUseMSAdminDefined is not set to Y");
              }
            }
        }
