  List accounts = plan.getAccountRequests();
  if (accounts != null) {
  for (AccountRequest req : accounts) {
    AccountRequest.Operation op = req.getOperation();
    if (op == AccountRequest.Operation.Create) {
      String userId = req.getNativeIdentity();
      if (userId != null) {
        req.setNativeIdentity(userId.toUpperCase());
      }
    }
    if (op == AccountRequest.Operation.Enable) {
      AttributeRequest att = new AttributeRequest("REVOKE_TYPE", "SUSPEND");
      req.add(att);
    }
    if (op == AccountRequest.Operation.Disable) {
      AttributeRequest att = new AttributeRequest("REVOKE_TYPE", "SUSPEND");
      req.add(att);
    }
    if(context != null)
    {
      IdentityService identityService = new IdentityService(context);
      if(application != null)
      {
        String shouldUseMSAdminDefined = (String) application.getAttributeValue("useMSAdminDefined");
        String msAdmin=null;
        if(Util.isNotNullOrEmpty(shouldUseMSAdminDefined)) 
        {
          if(shouldUseMSAdminDefined.equals("Y"))
          {
            List idnList = plan.getRequesters();
            if(idnList!=null)
            {
              if(!idnList.isEmpty())
              {
                Identity idn=(Identity) idnList.get(0);
                List lnkList = identityService.getLinks(idn, application);
                if(lnkList!=null)
                {
                  if(!lnkList.isEmpty())
                  {
                    Link lnk = (Link) lnkList.get(0);
                    //System.out.println("Adding msAdmin in provisioning plan.");
                    req.addArgument("msAdmin", lnk.getDisplayableName());
                  }
                }
                else
                  System.out.println("Requester does not have a link from application " + application.getName());
              }
            }
            else
              System.out.println("No requester found.");
          }
